parameters.systemMessage:
You are the LLM Controller for Project Jesse ‚Äî Trend Analysis. Plan an autonomous run that: (1) defines trending skill selection policy, (2) sets forecasting and topic-clustering policies, (3) defines causal-inference heuristics to separate signal from noise, (4) prescribes an early-identification scoring rubric. Output JSON ONLY:
{
  "selection_policy": {"min_freq": {{$node['Config'].json.MIN_FREQ || 30}}, "top_n": {{$node['Config'].json.TOP_N_SKILLS || 25}}, "growth_metric": "slope_over_mean"},
  "forecast_policy": {"horizon_days": {{$node['Config'].json.FORECAST_HORIZON_DAYS || 90}}, "method": "robust-linear + seasonality-notes"},
  "topic_policy": {"cluster_k": 6, "label_style": "concise 1-3 words", "use_definitions": true},
  "causal_policy": {"accept_threshold": 0.6, "notes": "compare against baseline posting volume to discount seasonality"},
  "early_id_policy": {"weights": {"growth":0.4, "forecast_delta":0.35, "novelty":0.15, "causal_signal":0.10}, "novelty_low_base": true}
}

parameters.functionCode:
function asArr(x){if(!x)return[]; if(Array.isArray(x))return x; if(x.data){try{return JSON.parse(x.data)}catch{}} return x.items||x.results||x.records||x;}
function ymd(d){const z=n=>('0'+n).slice(-2); return d.getFullYear()+'-'+z(d.getMonth()+1)+'-'+z(d.getDate());}
function parseDateAny(r){const c=[r.posted_date,r.postedDate,r.date,r.created_at,r.createdAt,r.postedAt]; for(const v of c){ if(!v) continue; const d=new Date(v); if(!isNaN(d)) return d; } return null;}
const WINDOW_DAYS=parseInt($node['Config'].json.WINDOW_DAYS||365,10);
const MIN_FREQ=parseInt($node['Config'].json.MIN_FREQ||30,10);
const now=new Date(); const start=new Date(now.getTime()-WINDOW_DAYS*86400000);
const jobs=asArr($node['Fetch Job Postings (window)'].json);
// Build baseline postings per day and per-skill counts
const baseline=new Map(); // day -> count
const bySkill=new Map();  // skill -> Map(day->count)
for(const r of jobs){ const d=parseDateAny(r); if(!d) continue; if(d<start) continue; const day=ymd(d); baseline.set(day,(baseline.get(day)||0)+1); const skills=Array.isArray(r.skills)? r.skills : (r.skill? [r.skill]: []); for(let s of skills){ if(!s) continue; s=String(s).trim(); if(!s) continue; const m=bySkill.get(s)||new Map(); m.set(day,(m.get(day)||0)+1); bySkill.set(s,m); }}
// build full day index
const days=[]; for(let i=0;i<=WINDOW_DAYS;i++){ const d=new Date(start.getTime()+i*86400000); days.push(ymd(d)); }
function seriesFromMap(m){return days.map((day,idx)=>({ t:day, x:idx, y:(m.get(day)||0) }));}
const baselineSeries=seriesFromMap(baseline);
// summarize each skill
const summary=[]; const seriesBySkill={};
for(const [skill,m] of bySkill.entries()){ const s=seriesFromMap(m); const n=s.length; const mean=s.reduce((a,b)=>a+b.y,0)/Math.max(1,n); // linear regression y ~ a + b*x
 let sumx=0,sumy=0,sumxy=0,sumxx=0; for(const p of s){ sumx+=p.x; sumy+=p.y; sumxy+=p.x*p.y; sumxx+=p.x*p.x; }
 const denom=(n*sumxx - sumx*sumx)||1; const slope=(n*sumxy - sumx*sumy)/denom; const growth=slope/Math.max(1e-6,mean);
 const total=s.reduce((a,b)=>a+b.y,0); if(total>=MIN_FREQ){ summary.push({skill,total,mean,slope,growth}); seriesBySkill[skill]=s; }
}
summary.sort((a,b)=>b.growth-a.growth);
return [{ json: { days, baselineSeries, seriesBySkill, summary } }];

const s=$json.series||[]; const H=parseInt($node['Config'].json.FORECAST_HORIZON_DAYS||90,10); if(!s.length){return [{json:{forecast:[],trend_label:'unknown',r2:0,slope:0}}];}
const n=s.length; let sumx=0,sumy=0,sumxy=0,sumxx=0; for(const p of s){ sumx+=p.x; sumy+=p.y; sumxy+=p.x*p.y; sumxx+=p.x*p.x; }
const denom=(n*sumxx - sumx*sumx)||1; const slope=(n*sumxy - sumx*sumy)/denom; const intercept=(sumy - slope*sumx)/n; // R^2
let ssTot=0, ssRes=0; const ybar=sumy/n; for(const p of s){ const yhat=intercept + slope*p.x; ssTot+=(p.y - ybar)*(p.y - ybar); ssRes+=(p.y - yhat)*(p.y - yhat); }
const r2 = ssTot>0 ? (1 - ssRes/ssTot) : 0; // forecast
const lastIndex = s[s.length-1].x; const forecast=[]; for(let i=1;i<=H;i++){ const x=lastIndex+i; const y=Math.max(0, intercept + slope*x); const t=new Date(s[0].t); t.setDate(t.getDate()+x); const ymd = (d=>{const z=n=>('0'+n).slice(-2); return d.getFullYear()+'-'+z(d.getMonth()+1)+'-'+z(d.getDate());})(t); forecast.push({ t: ymd, y: Number(y.toFixed(3)) }); }
let trend='stable'; if(slope>0.02) trend='rising'; if(slope<-0.02) trend='falling';
return [{ json: { forecast, trend_label: trend, r2: Number(r2.toFixed(4)), slope: Number(slope.toFixed(5)) } }];

