parameters.functionCode:
function safe(j){try{return JSON.parse(j.data||j);}catch{return j;}}
const plan=$node['Parse Controller Plan'].json||{}; const weights=(plan.early_id_policy&&plan.early_id_policy.weights)||{growth:0.4,forecast_delta:0.35,novelty:0.15,causal_signal:0.10};
const stats=$json.stats||{}; const slope=Number(stats.slope||0); const mean=Number(stats.mean||0);
const forecast=$node['Forecast Time Series (JS)'].json.forecast||[]; const hist=$json.series||[]; const last=hist.length? hist[hist.length-1].y : 0; const end=forecast.length? forecast[forecast.length-1].y : 0; const delta=end - last; const deltaScore = Math.max(0, Math.min(1, delta / Math.max(1, last+1)));
const growthScore = Math.max(0, Math.min(1, slope / Math.max(0.01, mean+0.01)));
const noveltyScore = Math.max(0, Math.min(1, mean<=1 ? 1 : 1/Math.sqrt(mean)));
const causal = safe($node['LLM Causal Check'].json)||{}; const causalScore = Math.max(0, Math.min(1, (causal.is_signal?1:0) * (causal.p_confidence||0)));
const emergence = weights.growth*growthScore + weights.forecast_delta*deltaScore + weights.novelty*noveltyScore + weights.causal_signal*causalScore;
return [{ json: { growthScore:Number(growthScore.toFixed(4)), deltaScore:Number(deltaScore.toFixed(4)), noveltyScore:Number(noveltyScore.toFixed(4)), causalScore:Number(causalScore.toFixed(4)), emergence_score:Number(emergence.toFixed(4)) } }];

function safe(j){try{return JSON.parse(j.data||j);}catch{return j;}}
const clusterMap = safe($node['LLM Cluster Topics'].json).mapping || {};
const forecastNode = $node['Forecast Time Series (JS)'].json || {};
const causal = safe($node['LLM Causal Check'].json) || {};
const score = $node['Score Emergence'].json || {};
const trend = forecastNode.trend_label || 'stable';
const r2 = forecastNode.r2 || 0; const slope = forecastNode.slope || 0;
const record = {
  skill: $json.skill,
  topic_label: clusterMap[$json.skill] || null,
  window_days: parseInt($node['Config'].json.WINDOW_DAYS||365,10),
  forecast_horizon_days: parseInt($node['Config'].json.FORECAST_HORIZON_DAYS||90,10),
  stats: $json.stats,
  trend: { label: trend, r2, slope },
  forecast: forecastNode.forecast || [],
  causal: causal,
  emergence: score,
  accepted: (score.emergence_score||0) >= 0.6,
  run_ts: new Date().toISOString()
};
return [{ json: { record } }];

